import React, { Component } from 'react'

import { BACKEND } from '../../config'

/*
    player info page
 */

class Players extends Component {


    constructor( props ) {
        super( props );
        this.state = {
            player : {
                id : this.props.match.params.playerId,
                name : null,
                height : null,
                weight : null,
                dob : null
            }
        };
        this.getPlayerInfo = this.getPlayerInfo.bind( this );
    }

    componentDidMount() {
        this.getPlayerInfo();
    }

    getPlayerInfo = function() {
        let url = BACKEND + '/player/' + this.state.player.id;
        fetch( url ).then( res => {
            return res.json();
        }).then( data => {
            console.log(data);
            this.setState( { player : data} );
        })
    };

    render() {
        return (
            <div>
                <div className='playerInfo'>
                    { this.state.player.id }
                    { this.state.player.dob }

                </div>
                <div>
                    <table>
                        <thead>
                            <tr>
                                <th>DATE</th>
                                <th>GAME</th>
                                <th>PTS</th>
                                <th>REB</th>
                                <th>AST</th>
                                <th>STL</th>
                                <th>BLK</th>
                            </tr>
                        </thead>
                    </table>
                </div>
            </div>
        );
    }
}

export default Players
