import React, { Component } from 'react'

import { BACKEND } from '../../config'

/*
    player info page
 */

class Players extends Component {


    constructor( props ) {
        super( props );
        this.state = {
            player : {
                id : this.props.match.params.playerId,
                name : null,
                height : null,
                weight : null,
                dob : null
            },
            page : 0,
            stats : []
        };
        this.getPlayerInfo = this.getPlayerInfo.bind( this );
        this.getStats = this.getStats.bind( this );
    }

    componentDidMount() {
        this.getPlayerInfo();
        this.getStats( this.state.player.id, 0 );
    }

    getPlayerInfo = function() {
        let url = BACKEND + '/player/' + this.state.player.id;
        fetch( url ).then( res => {
            return res.json();
        }).then( data => {
            this.setState( { player : data} );
        })
    };

    getStats = function( id, page ) {
        let url = BACKEND + `/stats/${ id }?page=${ page }`;
        fetch( url ).then( res => {
            return res.json();
        }).then( data => {
            if ( !data.err ) {
                console.log(data);
                this.setState({ stats : data })
            }
        })
    };

    render() {
        return (
            <div>
                <div className='playerInfo'>
                    { this.state.player.id }
                    { this.state.player.dob }

                </div>
                <div>
                    <table>
                        <thead>
                            <tr>
                                <th>DATE</th>
                                <th>GAME</th>
                                <th>PTS</th>
                                <th>REB</th>
                                <th>AST</th>
                                <th>STL</th>
                                <th>BLK</th>
                                <th>TOV</th>
                                <th>3PA</th>
                                <th>3PM</th>
                                <th>FGA</th>
                                <th>FGM</th>
                                <th>MIN</th>
                            </tr>
                        </thead>
                        <tbody>
                            { this.state.stats.map( ( element, idx) => {
                                // let background = '';
                                // if ( idx % 2 === 0) {
                                //     background = 'background-color: white';
                                // } else {
                                //     background = 'background-color: gray';
                                // }
                                return (
                                    <tr>
                                        <th>{ element.date }</th>
                                        <th>{ `${ element.team1_id } VS ${ element.team2_id }` }</th>
                                        <th>{ element.scores }</th>
                                        <th>{ element.rebounds }</th>
                                        <th>{ element.assists }</th>
                                        <th>{ element.steals }</th>
                                    </tr>
                                )
                            } ) }
                        </tbody>
                    </table>
                </div>
            </div>
        );
    }
}

export default Players
