import React, { Component } from 'react'
import { Link } from 'react-router-dom'
import Header from '../../components/header'

import { BACKEND } from '../../config'
import './player.css'

class Player extends Component {

    constructor( props ) {
        super( props );
        this.state = {
            name : '',
            results : []
        };
        this.handleInputChange = this.handleInputChange.bind( this );
        this.handleClick = this.handleClick.bind( this );
        this.search = this.search.bind( this );
    }

    handleInputChange = function( event ) {
        this.setState( { name : event.target.value } );
        this.search();
    };


    search = function() {
        if ( this.state.name === '' ) {
            return;
        }
        let pattern = this.state.name.replace( ' ', '%' );
        let url = BACKEND + 'player/name/' + pattern;
        fetch( url ).then( res => {
            return res.json();
        }).then( data  => {
            this.setState( { results : data} );
            console.log(data);
        })

    };

    handleClick = function() {

    };

    render() {
        return (
            <div>
                <Header/>
                <div>
                    <input onChange={ this.handleInputChange } className='searchPlayer'/>
                    <button onClick={ this.handleClick }>Search for player</button>
                </div>
                <div className='select'>
                    { this.state.results.map( ( element, index ) => {
                        return (
                            <div> <Link to={ '/players/' + element.id }>{ element.name }</Link> </div>

                        )
                    } ) }
                </div>

                <div className='searchResults'>
                    { this.state.results.map( ( element, index ) => {
                        return (
                            <div>
                                <img src={ element.iamge }/>
                                <Link to={ '/players/' + element.id }>{ element.name }</Link>
                            </div>

                        )
                    } ) }
                </div>


            </div>
        )
    }
}

export default Player
