import React, { Component } from 'react'

import { BACKEND } from '../../config'

import './login.css'

class Login extends Component {

    constructor( props ) {
        super( props );
        this.state = {
          'Email' : '',
          'Password' : ''
        };
        this.handleSubmit = this.handleSubmit.bind( this );
        this.handleInputChange = this.handleInputChange.bind( this );
    }

    handleInputChange = function( event ){
        if ( event.target.className === 'Email' ){
            this.setState({'Email' : event.target.value} )
        } else {
            this.setState({'Password' : event.target.value} )
        }
    };

    handleSubmit = function() {
        alert(this.state.Email + this.state.Password)
        let data = {username : this.state.Email, password: this.state.Password};
        let url = BACKEND + '/users';
        fetch(url, {method: "POST",body: JSON.stringify(data)}).then(res=>{return res.json()}).then(data=>{console.log(data)})
    };

    render() {
        return (
            <div className='outer'>
                <div className='inner'>
                    <h1>Login</h1>
                    <form onSubmit={ this.handleSubmit }>
                        <p>Email Address</p>
                        <input type='text'
                               className='Email'
                               placeholder='Email'
                               value={ this.state.Email }
                               onChange={ this.handleInputChange }
                        />
                        <p>Password</p>
                        <input type='password'
                               className='Password'
                               placeholder='Password'
                               value={ this.state.Password }
                               onChange={ this.handleInputChange }/>
                        <p></p>
                        <button>Submit</button>
                        <p className='text'>Don't have an account?
                            <a href='/register'>Create one</a>
                        </p>
                    </form>
                </div>
            </div>
        );
    }
}

export default Login
