import React, { Component } from 'react'

import { BACKEND } from '../../config'
import TeamInfo from './teamInfo'

class TeamList extends Component {

    constructor( props ) {
        super( props );
        this.state = {
            team : '0',
            players : [],
            teams : []
        };
        this.chooseTeam = this.chooseTeam.bind( this );
        this.getTeams = this.getTeams.bind( this );


    }

    componentDidMount() {
        this.getTeams();
    }

    getTeams = function() {
        let url = BACKEND + 'teamList';
        fetch( url ).then( res => {
            return res.json();
        }).then( data => {
            this.setState( {teams : data} );
            console.log(this.state);
        });
    };

    chooseTeam = function( event ) {
        // let id = event.target.id;
        console.log(event.target.id)
    };

    render() {
        return (
            <div>
                <div>
                    {
                        this.state.teams.map( ( element ) => {
                            return (<div id={ element.id } onClick={ this.chooseTeam }> { element.name } </div>)
                        } )
                    }
                </div>
                < TeamInfo players={ this.state.players }/>
            </div>
        )
    }
}

export default TeamList;
